// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.1;

interface IERC20 {
  function transfer(
    address from,
    address to,
    uint256 amount
  ) external returns (bool);

  function transferFrom(
    address from,
    address to,
    uint256 amount
  ) external returns (bool);

  function balanceOf(address account) external view returns (uint256);

  function approve(address spender, uint256 amount) external returns (bool);

  function allowance(
    address owner,
    address spender
  ) external view returns (uint256);

  event Approval(address indexed owner, address indexed spender, uint256 value);

  event Transfer(address indexed from, address indexed to, uint256 value);
}

contract SiliquaCoin is IERC20 {
  address public owner;
  string public name;
  string public symbol;
  uint8 public decimals;
  uint256 public totalSupply;

  mapping(address => uint256) private balances;
  mapping(address => mapping(address => uint256)) private allowances;

  constructor(
    string memory _name,
    string memory _symbol,
    uint8 _decimals,
    uint256 initialSupply
  ) {
    name = _name;
    symbol = _symbol;
    decimals = _decimals;
    totalSupply = initialSupply;
    balances[msg.sender] = initialSupply;
    owner = msg.sender;
  }

  function balanceOf(address account) external view override returns (uint256) {
    return balances[account];
  }

  function transfer(
    address from,
    address to,
    uint256 amount
  ) external override returns (bool) {
    require(balances[from] >= amount, "Insufficient balance");
    balances[from] -= amount;
    balances[to] += amount;
    emit Transfer(from, to, amount);
    return true;
  }

  function transferFrom(
    address from,
    address to,
    uint256 amount
  ) external override returns (bool) {
    require(amount <= balances[from], "Insufficient balance");
    require(amount <= allowances[from][msg.sender], "Insufficient allowance");
    balances[from] -= amount;
    balances[to] += amount;
    allowances[from][msg.sender] -= amount;
    emit Transfer(from, to, amount);
    return true;
  }

  function approve(
    address spender,
    uint256 amount
  ) external override returns (bool) {
    allowances[msg.sender][spender] = amount;
    emit Approval(msg.sender, spender, amount);
    return true;
  }

  function allowance(
    address tokenOwner,
    address spender
  ) external view override returns (uint256) {
    return allowances[tokenOwner][spender];
  }

  function burn(uint256 amount) external returns (bool) {
    require(msg.sender == owner, "Only the owner can mint new tokens");
    require(amount > 0, "Invalid amount");
    require(amount <= balances[msg.sender], "Insufficient balance");

    balances[msg.sender] -= amount;
    totalSupply -= amount;
    emit Transfer(msg.sender, address(0), amount);
    return true;
  }

  function mint(address to, uint256 amount) external returns (bool) {
    require(msg.sender == owner, "Only the owner can mint new tokens");

    totalSupply += amount;
    balances[to] += amount;
    emit Transfer(address(0), to, amount);
    return true;
  }
}

